INSERT INTO `article` (`id`, `title`, `alias`, `description`, `body`, `createtime`, `updatetime`, `uid`, `cid`, `tags`) VALUES
(1, 'Using File Image Behavior', '', '\r\n	Yii File v&agrave; Image AR Behavior cho ph&eacute;p li&ecirc;n kết c&aacute;c b&agrave;i viết tự động với c&aacute;c file v&agrave; ảnh minh họa một c&aacute;ch tương đối dễ d&agrave;ng.\r\n\r\n	Phần dưới đ&acirc;y tr&igrave;nh b&agrave;y c&aacute;ch sử d', '<p>\r\n	Yii File v&agrave; Image AR Behavior cho ph&eacute;p li&ecirc;n kết c&aacute;c b&agrave;i viết tự động với c&aacute;c file v&agrave; ảnh minh họa một c&aacute;ch tương đối dễ d&agrave;ng.</p>\r\n<p>\r\n	Phần dưới đ&acirc;y tr&igrave;nh b&agrave;y c&aacute;ch sử dụng ch&uacute;ng.</p>\r\n<h3>\r\n	FileARBehavior</h3>\r\n<p>\r\n	Trong v&iacute; dụ dưới đ&acirc;y, mỗi 1 m&oacute;n ăn tương ứng với 1 file chứa c&ocirc;ng thức nấu ăn:</p>\r\n<h4>\r\n	Th&ecirc;m Behavior v&agrave;o Model</h4>\r\n<pre>\r\n/** @file Dish.php*/\r\nclass Dish extends CActiveRecord {\r\n    public $recipe; // used by the form to send the file.\r\n\r\n    public function rules()\r\n    {\r\n        return array(\r\n            // ...\r\n            // for the form too\r\n            array(&#39;recipe&#39;, &#39;file&#39;, &#39;types&#39; =&gt; &#39;pdf, txt&#39;),\r\n            // ...\r\n        );\r\n    }\r\n\r\n    public function behaviors() {\r\n        return array(\r\n            &#39;recipeBehavior&#39; =&gt; array(\r\n                &#39;class&#39; =&gt; &#39;FileARBehavior&#39;,\r\n                &#39;attribute&#39; =&gt; &#39;recipe&#39;, // this must exist\r\n                &#39;extension&#39; =&gt; &#39;pdf, txt&#39;, // possible extensions, comma separated\r\n                &#39;prefix&#39; =&gt; &#39;recipe_&#39;, // if you want a prefix\r\n                &#39;relativeWebRootFolder&#39; =&gt; &#39;files/recipes&#39;, // this folder must exist\r\n                //&#39;defaultName&#39; =&gt; &#39;default&#39;, // you can also use a default file (see Ingredients with image below).\r\n            )\r\n        );\r\n    }\r\n}\r\n</pre>\r\n<h4>\r\n	Th&ecirc;m trường v&agrave;o Form</h4>\r\n<pre>\r\n$form=$this-&gt;beginWidget(&#39;CActiveForm&#39;, array(\r\n    // ...\r\n    &#39;htmlOptions&#39; =&gt; array(&#39;enctype&#39;=&gt;&#39;multipart/form-data&#39;), // don&#39;t forget this option\r\n));\r\n\r\necho $form-&gt;fileField($model,&#39;recipe&#39;);</pre>\r\n<h4>\r\n	Lấy dữ liệu về file đ&iacute;nh k&egrave;m</h4>\r\n<pre>\r\n$model-&gt;recipeBehavior-&gt;getFileUrl();\r\n$model-&gt;recipeBehavior-&gt;getFilePath();\r\n</pre>\r\n<h3>\r\n	ImageARBehavior</h3>\r\n<p>\r\n	Thay đổi lại cấu h&igrave;nh cho Behavior trong model:</p>\r\n<pre>\r\n    public function rules()\r\n\r\n    {\r\n        return array(\r\n            // ...\r\n            // for the form too\r\n            array(&#39;recipeImg&#39;, &#39;file&#39;, &#39;types&#39; =&gt; &#39;png, gif, jpg&#39;, &#39;allowEmpty&#39; =&gt; true),\r\n            // ...\r\n        );\r\n    }\r\n\r\n    public function behaviors() {\r\n        return array(\r\n            &#39;recipeImgBehavior&#39; =&gt; array(\r\n                &#39;class&#39; =&gt; &#39;ImageARBehavior&#39;,\r\n                &#39;attribute&#39; =&gt; &#39;recipeImg&#39;, // this must exist\r\n                &#39;extension&#39; =&gt; &#39;png, gif, jpg&#39;, // possible extensions, comma separated\r\n                &#39;prefix&#39; =&gt; &#39;img_&#39;,\r\n                &#39;relativeWebRootFolder&#39; =&gt; &#39;files/recipes&#39;, // this folder must exist\r\n\r\n                # &#39;forceExt&#39; =&gt; png, // this is the default, every saved image will be a png one.\r\n                # Set to null if you want to keep the original format\r\n\r\n                &#39;useImageMagick&#39; =&gt; &#39;/usr/bin&#39;, # I want to use imagemagick instead of GD, and\r\n                # it is located in /usr/bin on my computer.\r\n\r\n                // this will define formats for the image.\r\n                // The format &#39;normal&#39; always exist. This is the default format, by default no\r\n                // suffix or no processing is enabled.\r\n                &#39;formats&#39; =&gt; array(\r\n                    // create a thumbnail grayscale format\r\n                    &#39;thumb&#39; =&gt; array(\r\n                        &#39;suffix&#39; =&gt; &#39;_thumb&#39;,\r\n                        &#39;process&#39; =&gt; array(&#39;resize&#39; =&gt; array(60, 60), &#39;grayscale&#39; =&gt; true),\r\n                    ),\r\n                    // create a large one (in fact, no resize is applied)\r\n                    &#39;large&#39; =&gt; array(\r\n                        &#39;suffix&#39; =&gt; &#39;_large&#39;,\r\n                    ),\r\n                    // and override the default :\r\n                    &#39;normal&#39; =&gt; array(\r\n                        &#39;process&#39; =&gt; array(&#39;resize&#39; =&gt; array(200, 200)),\r\n                    ),\r\n                ),\r\n\r\n                &#39;defaultName&#39; =&gt; &#39;default&#39;, // when no file is associated, this one is used by getFileUrl\r\n                // defaultName need to exist in the relativeWebRootFolder path, and prefixed by prefix,\r\n                // and with one of the possible extensions. if multiple formats are used, a default file must exist\r\n                // for each format. Name is constructed like this :\r\n                //     {prefix}{name of the default file}{suffix}{one of the extension}\r\n            )\r\n        );\r\n    }\r\n</pre>\r\n<h4>\r\n	Truy xuất dữ liệu</h4>\r\n<pre>\r\n$format = &#39;normal&#39;; // for this example you can use &#39;large&#39; or &#39;thumb&#39;\r\n$fileUrl = $model-&gt;recipeImgBehavior-&gt;getFileUrl($format);\r\n$filePath = $model-&gt;recipeBehavior-&gt;getFilePath($format);\r\n$allPaths = $model-&gt;recipeBehavior-&gt;getFilesPath();</pre>\r\n<h3>\r\n	FileARBehavior với h&agrave;m Processor</h3>\r\n<p>\r\n	Viết 1 lớp Processor v&agrave; đặt trong <em>extensions/processors/</em></p>\r\n<pre>\r\nclass MyProcessor {\r\n    private $_content\r\n    // constructor must take an argument\r\n    public function __construct($srcPath) {\r\n        $this-&gt;_content = file_get_contents($srcPath);\r\n    }\r\n\r\n    public function myProcessFunc1() {\r\n        // do something with $this-&gt;_content ...\r\n    }\r\n\r\n    public function myProcessFunc2($param1, $param2) {\r\n        // do something with $this-&gt;_content  and params ...\r\n    }\r\n\r\n    // this function must exist\r\n    public function save($outPath) {\r\n        file_put_contents($outPath, $this-&gt;_content);\r\n    }\r\n}</pre>\r\n<p>\r\n	Nh&uacute;ng Processor v&agrave;o trong Behavior</p>\r\n<pre>\r\n    public function behaviors() {\r\n        return array(\r\n            &#39;myBehavior&#39; =&gt; array(\r\n                &#39;class&#39; =&gt; &#39;FileARBehavior&#39;,\r\n                &#39;attribute&#39; =&gt; &#39;myAttr&#39;,\r\n                &#39;extension&#39; =&gt; &#39;txt&#39;,\r\n                &#39;relativeWebRootFolder&#39; =&gt; &#39;files/myPath&#39;,\r\n\r\n                &#39;processor&#39; =&gt; &#39;ext.processors.MyProcessor&#39;\r\n\r\n                // this will create two files per model\r\n                &#39;formats&#39; =&gt; array(\r\n                    &#39;test&#39; =&gt; array(\r\n                        &#39;process&#39; =&gt; array(\r\n                            &#39;myProcessFunc1&#39; =&gt; true,\r\n                        ),\r\n                    ),\r\n                    // and override the default :\r\n                    &#39;normal&#39; =&gt; array(\r\n                        &#39;process&#39; =&gt; array(\r\n                            // myProcessFunc2 will be called before myProcessFunc1 when saving\r\n                            &#39;myProcessFunc2&#39; =&gt; array(200, 200),\r\n                            &#39;myProcessFunc1&#39; =&gt; true,\r\n                        ),\r\n                    ),\r\n                ),\r\n\r\n                // or, if you only want one file but processed, override only normal:\r\n                //&#39;formats&#39; =&gt; array(\r\n                //    &#39;normal&#39; =&gt; array(\r\n                //        &#39;process&#39; =&gt; array(\r\n                //            // myProcessFunc2 will be called before myProcessFunc1 when saving\r\n                //            &#39;myProcessFunc2&#39; =&gt; array(200, 200),\r\n                //            &#39;myProcessFunc1&#39; =&gt; true,\r\n                //        ),\r\n                //    ),\r\n                //),\r\n            )\r\n        );\r\n    }</pre>\r\n<p>\r\n	&nbsp;</p>\r\n', 1309886792, 1309887087, 1, 2, 'processor, file, image, behavior'),
(2, 'Arraymodel', '', '\r\n	Giới thiệu \r\n\r\n	Đ&ocirc;i khi n&oacute; c&oacute; thể c&oacute; &iacute;ch để đưa dữ liệu tĩnh v&agrave;o một mảng kết hợp tr&ecirc;n hệ thống tập tin thay v&igrave; v&agrave;o cơ sở dữ liệu.  Điều n&agrave;y c&oacute; thể bao gồm một số loại dữ liệu c', '<h2 id="hh0">\r\n	<span style="">Giới thiệu </span></h2>\r\n<p>\r\n	<span>Đ&ocirc;i khi n&oacute; c&oacute; thể c&oacute; &iacute;ch để đưa dữ liệu tĩnh v&agrave;o một mảng kết hợp tr&ecirc;n hệ thống tập tin thay v&igrave; v&agrave;o cơ sở dữ liệu.</span> <span> Điều n&agrave;y c&oacute; thể bao gồm một số loại dữ liệu cấu h&igrave;nh m&agrave; cần phải được nạp mỗi lần hoặc lặp đi lặp lại dữ liệu tĩnh, như c&aacute;c th&agrave;nh phố, quốc gia, v&ugrave;ng, tỉnh, vv Thật kh&ocirc;ng may, trong Yii kh&ocirc;ng c&oacute; loại m&ocirc; h&igrave;nh để truy cập v&agrave; / hoặc thao t&aacute;c n&agrave;y loại dữ liệu .</span> <span> Tại thời điểm n&agrave;y, EArrayModel đến in</span></p>\r\n<h2 id="hh1">\r\n	<span>C&aacute;ch sử dụng </span></h2>\r\n<p>\r\n	<span>EArrayModel đ&atilde; được thiết kế theo c&aacute;ch như vậy m&agrave; cấu tr&uacute;c của lớp học l&agrave; gần như ch&iacute;nh x&aacute;c giống như CActiveRecord, trừ c&aacute;c chức năng cơ sở dữ liệu cụ thể.</span> <span> N&oacute; đ&atilde; x&aacute;c nhận, kịch bản, t&igrave;m kiếm, cứu (ch&egrave;n / cập nhật) v&agrave; x&oacute;a c&aacute;c phương ph&aacute;p, m&agrave; c&ograve;n l&agrave; sự kiện ghi đ&egrave; l&ecirc;n trước v&agrave; sau khi ...(). ...()</span> <span> M&ocirc; h&igrave;nh n&agrave;y cũng cho ph&eacute;p t&igrave;m kiếm dữ liệu bằng c&aacute;ch sử dụng điều kiện.</span> <span> Để hiểu việc sử dụng ch&iacute;nh của m&ocirc; h&igrave;nh n&agrave;y, t&ocirc;i muốn tham khảo c&aacute;c hướng dẫn về c&aacute;c m&ocirc; h&igrave;nh v&agrave; ghi lại hoạt động tr&ecirc;n trang web của khung Yii.</span></p>\r\n<p>\r\n	<span>EArrayModel (như mỗi Model Yii kh&aacute;c) cũng l&agrave;m việc với c&aacute;c thuộc t&iacute;nh.</span> <span> Những thuộc t&iacute;nh n&agrave;y l&agrave; li&ecirc;n kết với một phần tử mảng duy nhất.</span> <span> Bạn (thực sự <strong>phải)</strong> c&oacute; thể định nghĩa phần tử của mảng được li&ecirc;n kết với thuộc t&iacute;nh đ&oacute;.</span> <span> Bạn l&agrave;m điều n&agrave;y bằng c&aacute;ch x&aacute;c định cấu tr&uacute;c dữ liệu th&ocirc;ng qua trọng c&aacute;c arrayStructure phương ph&aacute;p () (Xem đoạn <em>Tạo c&aacute;c lớp m&ocirc; h&igrave;nh).</em></span> <span> B&ecirc;n cạnh những thuộc t&iacute;nh n&agrave;y th&ocirc;ng thường được x&aacute;c định trong cấu tr&uacute;c mảng, ch&uacute;ng t&ocirc;i cũng c&oacute; c&aacute;c thuộc t&iacute;nh id ma thuật.</span> <span> Id thuộc t&iacute;nh n&agrave;y lu&ocirc;n lu&ocirc;n hiện diện v&agrave; đề cập đến kho&aacute; duy nhất của một h&agrave;ng dữ liệu mảng.</span> <span> Id n&agrave;y cũng c&oacute; thể được thay đổi như tất cả c&aacute;c thuộc t&iacute;nh kh&aacute;c.</span> <span> Nếu id thay đổi đ&atilde; tồn tại trong c&aacute;c tập tin dữ liệu, sau đ&oacute; h&agrave;ng dữ liệu với id tương ứng l&agrave; nhận được ghi đ&egrave;.</span></p>\r\n<h3 id="hh2">\r\n	<span>Ch&egrave;n một h&agrave;ng dữ liệu mới </span></h3>\r\n<div class="hl-code">\r\n	<div class="hl-main">\r\n		<pre>\r\n<span class="hl-var">$model</span><span class="hl-code"> = </span><span class="hl-reserved">new</span> <span class="hl-identifier">City</span><span class="hl-code">;\r\n</span><span class="hl-var">$model</span><span class="hl-code">-&gt;</span><span class="hl-identifier">name</span><span class="hl-code"> = </span><span class="hl-quotes">&#39;</span><span class="hl-string">Amsterdam</span><span class="hl-quotes">&#39;</span><span class="hl-code">;\r\n</span><span class="hl-var">$model</span><span class="hl-code">-&gt;</span><span class="hl-identifier">position_x</span><span class="hl-code"> = </span><span class="hl-number">205</span><span class="hl-code">;\r\n</span><span class="hl-var">$model</span><span class="hl-code">-&gt;</span><span class="hl-identifier">position_y</span><span class="hl-code"> = </span><span class="hl-number">673</span><span class="hl-code">;\r\n</span><span class="hl-var">$model</span><span class="hl-code">-&gt;</span><span class="hl-identifier">save</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">;</span></pre>\r\n	</div>\r\n</div>\r\n<h3 id="hh3">\r\n	<span style="">Cập nhật một h&agrave;ng dữ liệu hiện c&oacute; </span></h3>\r\n<div class="hl-code">\r\n	<div class="hl-main">\r\n		<pre>\r\n<span class="hl-var">$model</span><span class="hl-code"> = </span><span class="hl-identifier">City</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">-&gt;</span><span class="hl-identifier">findById</span><span class="hl-brackets">(</span><span class="hl-number">1</span><span class="hl-brackets">)</span><span class="hl-code">;\r\n</span><span class="hl-var">$model</span><span class="hl-code">-&gt;</span><span class="hl-identifier">position_x</span><span class="hl-code"> = </span><span class="hl-number">100</span><span class="hl-code">;\r\n</span><span class="hl-var">$model</span><span class="hl-code">-&gt;</span><span class="hl-identifier">position_y</span><span class="hl-code"> = </span><span class="hl-number">201</span><span class="hl-code">;\r\n</span><span class="hl-var">$model</span><span class="hl-code">-&gt;</span><span class="hl-identifier">save</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">;</span></pre>\r\n	</div>\r\n</div>\r\n<h3 id="hh4">\r\n	X&oacute;a một h&agrave;ng dữ liệu hiện c&oacute;</h3>\r\n<div class="hl-code">\r\n	<div class="hl-main">\r\n		<pre>\r\n<span class="hl-var">$model</span><span class="hl-code"> = </span><span class="hl-identifier">City</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">-&gt;</span><span class="hl-identifier">findById</span><span class="hl-brackets">(</span><span class="hl-number">1</span><span class="hl-brackets">)</span><span class="hl-code">;\r\n</span><span class="hl-var">$model</span><span class="hl-code">-&gt;</span><span class="hl-identifier">delete</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">;</span></pre>\r\n	</div>\r\n</div>\r\n<h3 id="hh5">\r\n	X&oacute;a nhiều h&agrave;ng dữ liệu</h3>\r\n<div class="hl-code">\r\n	<div class="hl-main">\r\n		<pre>\r\n<span class="hl-identifier">City</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">-&gt;</span><span class="hl-identifier">deleteAll</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">; </span><span class="hl-comment">//</span><span class="hl-comment"> Deletes all rows</span>\r\n<span class="hl-identifier">City</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">-&gt;</span><span class="hl-identifier">deleteAll</span><span class="hl-brackets">(</span><span class="hl-comment">/*</span><span class="hl-comment">condition</span><span class="hl-comment">*/</span><span class="hl-brackets">)</span><span class="hl-code">; </span><span class="hl-comment">//</span><span class="hl-comment"> Deletes all rows matched by the condition</span></pre>\r\n	</div>\r\n</div>\r\n<h3 id="hh6">\r\n	T&igrave;m c&aacute;c h&agrave;ng dữ liệu</h3>\r\n<div class="hl-code">\r\n	<div class="hl-main">\r\n		<pre>\r\n<span class="hl-var">$model</span><span class="hl-code"> = </span><span class="hl-identifier">City</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">-&gt;</span><span class="hl-identifier">findById</span><span class="hl-brackets">(</span><span class="hl-number">4</span><span class="hl-brackets">)</span><span class="hl-code">; </span><span class="hl-comment">//</span><span class="hl-comment"> Returns a single row with id 4</span>\r\n<span class="hl-var">$model</span><span class="hl-code"> = </span><span class="hl-identifier">City</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">-&gt;</span><span class="hl-identifier">find</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">; </span><span class="hl-comment">//</span><span class="hl-comment"> Returns the first found row</span>\r\n \r\n<span class="hl-var">$models</span><span class="hl-code"> = </span><span class="hl-identifier">City</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">-&gt;</span><span class="hl-identifier">findAll</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">; </span><span class="hl-comment">//</span><span class="hl-comment"> Returns all rows, conditions may be used</span>\r\n<span class="hl-var">$models</span><span class="hl-code"> = </span><span class="hl-identifier">City</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">-&gt;</span><span class="hl-identifier">findAll</span><span class="hl-brackets">(</span><span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-comment">/*</span><span class="hl-comment">condition</span><span class="hl-comment">*/</span><span class="hl-brackets">)</span><span class="hl-brackets">)</span><span class="hl-code">; </span><span class="hl-comment">//</span><span class="hl-comment"> Returns all rows matched by the condition</span>\r\n<span class="hl-var">$models</span><span class="hl-code"> = </span><span class="hl-identifier">City</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">-&gt;</span><span class="hl-identifier">findAll</span><span class="hl-brackets">(</span><span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-comment">/*</span><span class="hl-comment">condition</span><span class="hl-comment">*/</span><span class="hl-brackets">)</span><span class="hl-code">, </span><span class="hl-number">5</span><span class="hl-code">, </span><span class="hl-number">10</span><span class="hl-brackets">)</span><span class="hl-code">; </span><span class="hl-comment">//</span><span class="hl-comment"> Returns 5 rows matched by the condition, beginning on row number 10 (does exactly work like LIMIT in MySQL)</span></pre>\r\n	</div>\r\n</div>\r\n<h3 id="hh7">\r\n	<span style="">Sử dụng điều kiện để t&igrave;m dữ liệu mảng </span></h3>\r\n<p>\r\n	<span>Một điều kiện cần l&agrave; một mảng v&agrave; phải bao gồm b&aacute;o c&aacute;o c&oacute; điều kiện với &#39;thuộc t&iacute;nh =&gt; gi&aacute; trị&#39; cấu tr&uacute;c.</span> <span> Mỗi tuy&ecirc;n bố c&oacute; điều kiện cần phải được t&aacute;ch ra bởi một AND hoặc OR.</span> <span> T&igrave;nh trạng hoạt động như b&igrave;nh thường IF b&aacute;o c&aacute;o trong PHP v&agrave; c&oacute; thể chứa to&aacute;n tử so s&aacute;nh sau đ&acirc;y:</span></p>\r\n<pre>\r\n <span> &gt;,&gt; =, &lt;, &lt;=, = (Hoặc ==), =,! ===,! ==,%,!%</span>\r\n</pre>\r\n<p>\r\n	<span>Ngo&agrave;i PHP c&oacute; hai nh&agrave; khai th&aacute;c nhiều hơn,% v&agrave;%!.</span> <span> C&aacute;c nh&agrave; khai th&aacute;c đứng cho tương ứng &quot;chứa&quot; v&agrave; &quot;kh&ocirc;ng c&oacute;&quot;.</span> <span> N&oacute; sẽ kiểm tra xem c&aacute;c chuỗi cho trước c&oacute; thể được t&igrave;m thấy hoặc kh&ocirc;ng t&igrave;m thấy b&ecirc;n trong gi&aacute; trị của một thuộc t&iacute;nh cụ thể.</span> <span> C&aacute;c nh&agrave; điều h&agrave;nh sẽ t&igrave;m kiếm c&aacute;c trường hợp kh&ocirc;ng nhạy cảm.</span></p>\r\n<p>\r\n	<span>Khi kh&ocirc;ng c&oacute; nh&agrave; điều h&agrave;nh so s&aacute;nh đ&atilde; được sử dụng, điều kiện sẽ tự động chức năng như kết hợp ch&iacute;nh x&aacute;c (= hay ==).</span></p>\r\n<p>\r\n	<span>Cũng giống như trong PHP, c&aacute;c điều kiện c&oacute; thể chứa c&aacute;c phụ kiện, phụ phụ kiện, v&agrave; vv.</span> <span> N&oacute; c&oacute; thể được nhận ra bởi chỉ cần th&ecirc;m c&aacute;c mảng mới, c&oacute; b&aacute;o c&aacute;o c&oacute; điều kiện.</span> <span> Bằng c&aacute;ch n&agrave;y, một điều kiện v&ocirc; tận c&oacute; thể được tạo ra.</span> <span> Một số v&iacute; dụ:</span></p>\r\n<div class="hl-code">\r\n	<div class="hl-main">\r\n		<pre>\r\n<span class="hl-identifier">Contact</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">-&gt;</span><span class="hl-identifier">findAll</span><span class="hl-brackets">(</span><span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-quotes">&#39;</span><span class="hl-string">id</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-number">1</span><span class="hl-brackets">)</span><span class="hl-brackets">)</span><span class="hl-code">; </span><span class="hl-comment">//</span><span class="hl-comment"> Return data with ID 1 (exact matching)</span>\r\n<span class="hl-identifier">Contact</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">-&gt;</span><span class="hl-identifier">findAll</span><span class="hl-brackets">(</span><span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-quotes">&#39;</span><span class="hl-string">id &gt;</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-number">5</span><span class="hl-brackets">)</span><span class="hl-code">, </span><span class="hl-quotes">&#39;</span><span class="hl-string">AND</span><span class="hl-quotes">&#39;</span><span class="hl-code">, </span><span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-quotes">&#39;</span><span class="hl-string">id &gt;</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-number">15</span><span class="hl-brackets">)</span><span class="hl-brackets">)</span><span class="hl-code">; </span><span class="hl-comment">//</span><span class="hl-comment"> Matches data with an ID bigger than 5 and smaller than 15</span>\r\n<span class="hl-identifier">Contact</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">-&gt;</span><span class="hl-identifier">findAll</span><span class="hl-brackets">(</span><span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-quotes">&#39;</span><span class="hl-string">name %</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-quotes">&#39;</span><span class="hl-string">asd</span><span class="hl-quotes">&#39;</span><span class="hl-brackets">)</span><span class="hl-code">, </span><span class="hl-quotes">&#39;</span><span class="hl-string">OR</span><span class="hl-quotes">&#39;</span><span class="hl-code">, </span><span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-quotes">&#39;</span><span class="hl-string">name %</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-quotes">&#39;</span><span class="hl-string">qwe</span><span class="hl-quotes">&#39;</span><span class="hl-brackets">)</span><span class="hl-brackets">)</span><span class="hl-brackets">)</span><span class="hl-code">; </span><span class="hl-comment">//</span><span class="hl-comment"> Matches data which contains asd or qwe</span>\r\n<span class="hl-identifier">Contact</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">-&gt;</span><span class="hl-identifier">findAll</span><span class="hl-brackets">(</span><span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-quotes">&#39;</span><span class="hl-string">first_name</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-quotes">&#39;</span><span class="hl-string">John</span><span class="hl-quotes">&#39;</span><span class="hl-brackets">)</span><span class="hl-code">, </span><span class="hl-quotes">&#39;</span><span class="hl-string">OR</span><span class="hl-quotes">&#39;</span><span class="hl-code">, </span><span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-quotes">&#39;</span><span class="hl-string">first_name</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-quotes">&#39;</span><span class="hl-string">Matthew</span><span class="hl-quotes">&#39;</span><span class="hl-brackets">)</span><span class="hl-brackets">)</span><span class="hl-code">, </span><span class="hl-quotes">&#39;</span><span class="hl-string">AND</span><span class="hl-quotes">&#39;</span><span class="hl-code">, </span><span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-quotes">&#39;</span><span class="hl-string">last_name</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-quotes">&#39;</span><span class="hl-string">Smith</span><span class="hl-quotes">&#39;</span><span class="hl-brackets">)</span><span class="hl-brackets">)</span><span class="hl-brackets">)</span><span class="hl-code">; </span><span class="hl-comment">//</span><span class="hl-comment"> Matches data where the first name is John or Matthew and the last name is Smith</span></pre>\r\n	</div>\r\n</div>\r\n<p>\r\n	To make conditions a little bit more readable and shorter, you can leave out some array elements:</p>\r\n<p>\r\n	<strong>Normal usage:</strong></p>\r\n<div class="hl-code">\r\n	<div class="hl-main">\r\n		<pre>\r\n<span class="hl-identifier">Contact</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">-&gt;</span><span class="hl-identifier">findAll</span><span class="hl-brackets">(</span><span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-quotes">&#39;</span><span class="hl-string">id &gt;</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-number">5</span><span class="hl-brackets">)</span><span class="hl-brackets">)</span><span class="hl-brackets">)</span><span class="hl-code">;</span></pre>\r\n	</div>\r\n</div>\r\n<p>\r\n	<strong>Shorter usage</strong></p>\r\n<div class="hl-code">\r\n	<div class="hl-main">\r\n		<pre>\r\n<span class="hl-identifier">Contact</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">-&gt;</span><span class="hl-identifier">findAll</span><span class="hl-brackets">(</span><span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-quotes">&#39;</span><span class="hl-string">id &gt;</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-number">5</span><span class="hl-brackets">)</span><span class="hl-brackets">)</span><span class="hl-code">;</span></pre>\r\n	</div>\r\n</div>\r\n<p>\r\n	However, with the shorter usage you can&#39;t use the same attributes inside one condition statement. With the following example, only the last attribute will be checked:</p>\r\n<div class="hl-code">\r\n	<div class="hl-main">\r\n		<pre>\r\n<span class="hl-identifier">Contact</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">-&gt;</span><span class="hl-identifier">findAll</span><span class="hl-brackets">(</span><span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-quotes">&#39;</span><span class="hl-string">id</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-number">5</span><span class="hl-code">, </span><span class="hl-quotes">&#39;</span><span class="hl-string">OR</span><span class="hl-quotes">&#39;</span><span class="hl-code">, </span><span class="hl-quotes">&#39;</span><span class="hl-string">id</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-number">7</span><span class="hl-brackets">)</span><span class="hl-brackets">)</span><span class="hl-code">; </span><span class="hl-comment">//</span><span class="hl-comment"> Only row with ID 7 will be returned</span></pre>\r\n	</div>\r\n</div>\r\n<p>\r\n	The following on the other hand will work as aspected, because of the &gt;= and &lt;= comparison operators.</p>\r\n<div class="hl-code">\r\n	<div class="hl-main">\r\n		<pre>\r\n<span class="hl-identifier">Contact</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">-&gt;</span><span class="hl-identifier">findAll</span><span class="hl-brackets">(</span><span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-quotes">&#39;</span><span class="hl-string">id &gt;=</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-number">5</span><span class="hl-code">, </span><span class="hl-quotes">&#39;</span><span class="hl-string">AND</span><span class="hl-quotes">&#39;</span><span class="hl-code">, </span><span class="hl-quotes">&#39;</span><span class="hl-string">id &lt;=</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-number">7</span><span class="hl-brackets">)</span><span class="hl-brackets">)</span><span class="hl-code">; </span><span class="hl-comment">//</span><span class="hl-comment"> All rows with ID 5, 6 and 7 will be returned</span></pre>\r\n	</div>\r\n</div>\r\n<p>\r\n	This limitation is there because the condition &#39;array&#39; can&#39;t have two or more elements with the same key. The first id will be overwritten by the last defined id. In this case you still need to use the normal usage instead.</p>\r\n<h2 id="hh8">\r\n	<span style="">Tạo c&aacute;c lớp m&ocirc; h&igrave;nh </span></h2>\r\n<p>\r\n	<span style="">Mỗi lớp học phải mở rộng m&ocirc; h&igrave;nh mảng từ EArrayModel v&agrave; cần phải ghi đ&egrave; l&ecirc;n một v&agrave;i phương ph&aacute;p.</span> <span style=""> Cấu tr&uacute;c của lớp n&agrave;y kh&aacute;c với CActiveModel của t&ecirc;n tập tin phương ph&aacute;p () v&agrave; arrayStructure ().</span></p>\r\n<p>\r\n	<span>V&iacute; dụ về một lớp EArrayModel:</span></p>\r\n<div class="hl-code">\r\n	<div class="hl-main">\r\n		<pre>\r\n<span class="hl-inlinetags">&lt;?php</span>\r\n<span class="hl-reserved">class</span> <span class="hl-identifier">RankArray</span> <span class="hl-reserved">extends</span> <span class="hl-identifier">ArrayModel</span>\r\n<span class="hl-brackets">{</span>\r\n    <span class="hl-comment">/*</span><span class="hl-comment">*\r\n    * Returns the static model of the specified AM class.\r\n    *</span><span class="hl-inlinedoc"> @return </span><span class="hl-comment">the static model class\r\n    </span><span class="hl-comment">*/</span>\r\n    <span class="hl-reserved">public</span> <span class="hl-reserved">static</span> <span class="hl-reserved">function</span> <span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-var">$className</span><span class="hl-code">=</span><span class="hl-reserved">__CLASS__</span><span class="hl-brackets">)</span>\r\n    <span class="hl-brackets">{</span>\r\n        <span class="hl-reserved">return</span> <span class="hl-identifier">parent</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-var">$className</span><span class="hl-brackets">)</span><span class="hl-code">;\r\n    </span><span class="hl-brackets">}</span>\r\n \r\n    <span class="hl-comment">/*</span><span class="hl-comment">*\r\n    *</span><span class="hl-inlinedoc"> @return </span><span class="hl-comment">string the associated array data file location (must be writable, to make use of the saving functionalities)\r\n    </span><span class="hl-comment">*/</span>\r\n    <span class="hl-reserved">public</span> <span class="hl-reserved">function</span> <span class="hl-identifier">fileName</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span>\r\n    <span class="hl-brackets">{</span>\r\n        <span class="hl-reserved">return</span> <span class="hl-quotes">&#39;</span><span class="hl-string">application.data.ranks</span><span class="hl-quotes">&#39;</span><span class="hl-code">;\r\n    </span><span class="hl-brackets">}</span>\r\n \r\n    <span class="hl-comment">/*</span><span class="hl-comment">*\r\n    *</span><span class="hl-inlinedoc"> @return </span><span class="hl-comment">array the associated array structure (&#39;key&#39; =&gt; &#39;attribute&#39;). The list of\r\n    * available attributes will be based on this list.\r\n    </span><span class="hl-comment">*/</span>\r\n    <span class="hl-reserved">public</span> <span class="hl-reserved">function</span> <span class="hl-identifier">arrayStructure</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span>\r\n    <span class="hl-brackets">{</span>\r\n        <span class="hl-reserved">return</span> <span class="hl-reserved">array</span><span class="hl-brackets">(</span>\r\n            <span class="hl-quotes">&#39;</span><span class="hl-string">name</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-reserved">array</span><span class="hl-brackets">(</span>\r\n                <span class="hl-quotes">&#39;</span><span class="hl-string">male</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-quotes">&#39;</span><span class="hl-string">name_male</span><span class="hl-quotes">&#39;</span><span class="hl-code">,\r\n                </span><span class="hl-quotes">&#39;</span><span class="hl-string">female</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-quotes">&#39;</span><span class="hl-string">name_female</span><span class="hl-quotes">&#39;</span><span class="hl-code">,\r\n            </span><span class="hl-brackets">)</span><span class="hl-code">,\r\n            </span><span class="hl-quotes">&#39;</span><span class="hl-string">points</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-quotes">&#39;</span><span class="hl-string">points</span><span class="hl-quotes">&#39;</span><span class="hl-code">,\r\n        </span><span class="hl-brackets">)</span><span class="hl-code">;\r\n    </span><span class="hl-brackets">}</span>\r\n \r\n    <span class="hl-comment">/*</span><span class="hl-comment">*\r\n    *</span><span class="hl-inlinedoc"> @return </span><span class="hl-comment">array validation rules for model attributes.\r\n    </span><span class="hl-comment">*/</span>\r\n    <span class="hl-reserved">public</span> <span class="hl-reserved">function</span> <span class="hl-identifier">rules</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span>\r\n    <span class="hl-brackets">{</span>\r\n        <span class="hl-reserved">return</span> <span class="hl-reserved">array</span><span class="hl-brackets">(</span>\r\n            <span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-quotes">&#39;</span><span class="hl-string">name_male, name_female</span><span class="hl-quotes">&#39;</span><span class="hl-code">, </span><span class="hl-quotes">&#39;</span><span class="hl-string">length</span><span class="hl-quotes">&#39;</span><span class="hl-code">, </span><span class="hl-quotes">&#39;</span><span class="hl-string">max</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-number">20</span><span class="hl-brackets">)</span><span class="hl-code">,\r\n        </span><span class="hl-brackets">)</span><span class="hl-code">;\r\n    </span><span class="hl-brackets">}</span>\r\n \r\n    <span class="hl-comment">/*</span><span class="hl-comment">*\r\n    *</span><span class="hl-inlinedoc"> @return </span><span class="hl-comment">array validation rules for model attributes.\r\n    </span><span class="hl-comment">*/</span>\r\n    <span class="hl-reserved">public</span> <span class="hl-reserved">function</span> <span class="hl-identifier">attributeLabels</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span>\r\n    <span class="hl-brackets">{</span>\r\n        <span class="hl-reserved">return</span> <span class="hl-reserved">array</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">;\r\n    </span><span class="hl-brackets">}</span>\r\n<span class="hl-brackets">}</span>\r\n<span class="hl-inlinetags">?&gt;</span></pre>\r\n	</div>\r\n</div>\r\n<h2 id="hh9">\r\n	<span>V&iacute; dụ tập tin dữ liệu li&ecirc;n quan </span></h2>\r\n<p>\r\n	<span style="">C&aacute;c tập tin dữ liệu được bảo vệ / dữ liệu / ranks.php, li&ecirc;n quan đến m&ocirc; h&igrave;nh RankArray của đoạn trước</span></p>\r\n<div class="hl-code">\r\n	<div class="hl-main">\r\n		<pre>\r\n<span class="hl-inlinetags">&lt;?php</span>\r\n<span class="hl-comment">/*</span><span class="hl-comment">*\r\n* Data file generated by RankArray (ArrayModel)\r\n* Date: June 30, 2011, 9:57 pm\r\n*\r\n* Allowed data structure:\r\n*   array(\r\n*       &#39;name&#39; =&gt; array(\r\n*           &#39;male&#39; =&gt; &#39;name_male&#39;,\r\n*           &#39;female&#39; =&gt; &#39;name_female&#39;,\r\n*       ),\r\n*       &#39;points&#39; =&gt; &#39;points&#39;,\r\n*   )\r\n</span><span class="hl-comment">*/</span>\r\n<span class="hl-reserved">return</span> <span class="hl-reserved">array</span><span class="hl-brackets">(</span>\r\n    <span class="hl-number">1</span><span class="hl-code"> =&gt; </span><span class="hl-reserved">array</span><span class="hl-brackets">(</span>\r\n        <span class="hl-quotes">&#39;</span><span class="hl-string">name</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-reserved">array</span><span class="hl-brackets">(</span>\r\n            <span class="hl-quotes">&#39;</span><span class="hl-string">male</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-quotes">&#39;</span><span class="hl-string">Cleaner</span><span class="hl-quotes">&#39;</span><span class="hl-code">,\r\n            </span><span class="hl-quotes">&#39;</span><span class="hl-string">female</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-quotes">&#39;</span><span class="hl-string">Cleaner</span><span class="hl-quotes">&#39;</span><span class="hl-code">,\r\n        </span><span class="hl-brackets">)</span><span class="hl-code">,\r\n        </span><span class="hl-quotes">&#39;</span><span class="hl-string">points</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-number">5</span><span class="hl-code">,\r\n    </span><span class="hl-brackets">)</span><span class="hl-code">,\r\n    </span><span class="hl-number">2</span><span class="hl-code"> =&gt; </span><span class="hl-reserved">array</span><span class="hl-brackets">(</span>\r\n        <span class="hl-quotes">&#39;</span><span class="hl-string">name</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-reserved">array</span><span class="hl-brackets">(</span>\r\n            <span class="hl-quotes">&#39;</span><span class="hl-string">male</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-quotes">&#39;</span><span class="hl-string">Bagman</span><span class="hl-quotes">&#39;</span><span class="hl-code">,\r\n            </span><span class="hl-quotes">&#39;</span><span class="hl-string">female</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-quotes">&#39;</span><span class="hl-string">Bagman</span><span class="hl-quotes">&#39;</span><span class="hl-code">,\r\n        </span><span class="hl-brackets">)</span><span class="hl-code">,\r\n        </span><span class="hl-quotes">&#39;</span><span class="hl-string">points</span><span class="hl-quotes">&#39;</span><span class="hl-code"> =&gt; </span><span class="hl-number">50</span><span class="hl-code">,\r\n    </span><span class="hl-brackets">)</span><span class="hl-code">,\r\n</span><span class="hl-brackets">)</span><span class="hl-code">;</span></pre>\r\n	</div>\r\n</div>\r\n<h2 id="hh10">\r\n	Kh&aacute;c</h2>\r\n<div class="hl-code">\r\n	<div class="hl-main">\r\n		<pre>\r\n<span class="hl-var">$model</span><span class="hl-code">-&gt;</span><span class="hl-identifier">isNew</span><span class="hl-code">; </span><span class="hl-comment">//</span><span class="hl-comment"> Checks whether the current data model is new, works exactly like isNewRecord</span>\r\n<span class="hl-identifier">ModelName</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">-&gt;</span><span class="hl-identifier">getData</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">; </span><span class="hl-comment">//</span><span class="hl-comment"> Returns the raw data for manual data manipulation</span>\r\n<span class="hl-identifier">ModelName</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">-&gt;</span><span class="hl-identifier">setData</span><span class="hl-brackets">(</span><span class="hl-var">$data</span><span class="hl-brackets">)</span><span class="hl-code">; </span><span class="hl-comment">//</span><span class="hl-comment"> Set raw data manually after manipulation</span>\r\n<span class="hl-identifier">ModelName</span><span class="hl-code">::</span><span class="hl-identifier">model</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">-&gt;</span><span class="hl-identifier">flushData</span><span class="hl-brackets">(</span><span class="hl-brackets">)</span><span class="hl-code">; </span><span class="hl-comment">//</span><span class="hl-comment"> Cleans the cached data and forces to renew the data on next data file access</span></pre>\r\n	</div>\r\n</div>\r\n<h2 id="hh11">\r\n	<span style="">Ghi ch&uacute; Kỹ thuật </span></h2>\r\n<ul>\r\n	<li>\r\n		<span>Dữ liệu được lưu trữ b&ecirc;n trong nhận được một biến tĩnh.</span> <span> Bằng c&aacute;ch n&agrave;y, c&aacute;c m&ocirc; h&igrave;nh sẽ kh&ocirc;ng truy cập hệ thống tập tin mỗi khi n&oacute; cần một số dữ liệu.</span> <span> C&aacute;c dữ liệu lưu trữ tất nhi&ecirc;n sẽ tự động được cập nhật sau khi tiết kiệm.</span></li>\r\n	<li>\r\n		<span style="">Khi ch&egrave;n, cập nhật hoặc x&oacute;a c&aacute;c h&agrave;ng dữ liệu, tập tin dữ liệu l&agrave; nhận được lưu lại mỗi lần.</span> <span> H&atilde;y cẩn thận khi bạn cần phải tiết kiệm rất nhiều m&ocirc; h&igrave;nh kh&aacute;c nhau c&ugrave;ng một l&uacute;c.</span></li>\r\n	<li>\r\n		<span>Sử dụng m&ocirc; h&igrave;nh n&agrave;y chỉ d&agrave;nh cho dữ liệu tĩnh c&oacute; thực sự cần phải được thay đổi kh&ocirc;ng thường xuy&ecirc;n.</span> <span> Đối với dữ liệu động n&oacute; sẽ được th&ecirc;m rất nhiều hiệu quả sử dụng cơ sở dữ liệu ghi lại hoạt động thay thế.</span> <span> Ngo&agrave;i ra, thay đổi dữ liệu tr&ecirc;n hệ thống tập tin sẽ kh&oacute;a c&aacute;c tập tin trong một khoảng thời gian rất ngắn.</span> <span> Điều đ&oacute; c&oacute; thể cung cấp cho c&aacute;c lỗi php tốt đẹp khi hai m&ocirc; h&igrave;nh tiết kiệm c&ugrave;ng một l&uacute;c.</span></li>\r\n</ul>\r\n<h2 id="hh12">\r\n	<span>C&agrave;i đặt </span></h2>\r\n<ol>\r\n	<li>\r\n		<span>Tr&iacute;ch xuất arraymodel từ kho lưu trữ để bảo vệ / mở rộng</span></li>\r\n	<li>\r\n		<span>Tạo một m&ocirc; h&igrave;nh cho mỗi tập tin dữ liệu mảng v&agrave; cấu h&igrave;nh n&oacute;</span></li>\r\n	<li>\r\n		<span>Tạo tập tin dữ liệu tương ứng v&agrave; cuối c&ugrave;ng l&agrave;m cho n&oacute; c&oacute; thể ghi.</span> <span> Bạn c&oacute; thể điền v&agrave;o c&aacute;c tập tin với nội dung &lt;php trở lại mảng ();?&gt; (Trong phi&ecirc;n bản mới nhất, đ&oacute; l&agrave; kh&ocirc;ng cần thiết nữa)</span></li>\r\n	<li>\r\n		<span>Bạn đang tắt để đi</span></li>\r\n</ol>\r\n<h2 id="hh13">\r\n	<span>Y&ecirc;u cầu </span></h2>\r\n<ul>\r\n	<li>\r\n		<span style="">Yii 1.1 hoặc ở tr&ecirc;n (thử nghiệm tr&ecirc;n 1.1.7)</span></li>\r\n	<li>\r\n		<span>Th&ocirc;ng thường</span></li>\r\n</ul>\r\n<h2 id="hh14">\r\n	<span>Todo </span></h2>\r\n<ul>\r\n	<li>\r\n		<span>Xử l&yacute; lỗi tốt hơn với những văn bản lỗi tốt đẹp</span></li>\r\n	<li>\r\n		<span>Cung cấp một nh&agrave; cung cấp dữ liệu tương ứng, như CActiveDataProvider.</span> <span> Như một giải ph&aacute;p tạm thời, CArrayDataProvider kết hợp với $ yourArrayModel-&gt; getdata () c&oacute; thể được sử dụng.</span></li>\r\n</ul>\r\n<h2 id="hh15">\r\n	<span>T&agrave;i </span></h2>\r\n<ul>\r\n	<li>\r\n		<span>http://www.yiiframework.com/doc/guide/1.1/en/basics.model</span></li>\r\n	<li>\r\n		<span>http://www.yiiframework.com/doc/guide/1.1/en/form.model</span></li>\r\n	<li>\r\n		<span>http://www.yiiframework.com/doc/guide/1.1/en/database.ar</span></li>\r\n</ul>\r\n<p>\r\n	<span>Ngo&agrave;i ra, t&ocirc;i muốn cảm ơn c&aacute;c nh&agrave; ph&aacute;t triển đằng sau Yii cho việc tạo khu&ocirc;n khổ lớn như vậy!</span> <span> ;-)</span></p>\r\n', 1309888127, 1309888659, 1, 2, 'model, active record, activerecord, array, data, file, find, validate, save');
INSERT INTO `article` (`id`, `title`, `alias`, `description`, `body`, `createtime`, `updatetime`, `uid`, `cid`, `tags`) VALUES
(3, 'Xây dựng hàm cấu hình cho WebBaseController', '', '\r\n	D&ugrave;ng CFormModel\r\n\r\n	Th&ocirc;ng qua lớp CFormModel, ta c&oacute; thể tạo m&ocirc; h&igrave;nh Form để kiểm tra dữ liệu, nếu thấy dữ liệu ph&ugrave; hợp th&igrave; thực hiện việc lưu gi&aacute; trị v&agrave;o bảng Setting.\r\n\r\n	D&ugrave;ng CActive', '<h2>\r\n	D&ugrave;ng CFormModel</h2>\r\n<p>\r\n	Th&ocirc;ng qua lớp CFormModel, ta c&oacute; thể tạo m&ocirc; h&igrave;nh Form để kiểm tra dữ liệu, nếu thấy dữ liệu ph&ugrave; hợp th&igrave; thực hiện việc lưu gi&aacute; trị v&agrave;o bảng Setting.</p>\r\n<h2>\r\n	D&ugrave;ng CActiveRecord với scenario <em>Configure</em></h2>\r\n<p>\r\n	Dựa v&agrave;o t&iacute;nh năng scenario của Model, ta c&oacute; thể sử dụng lại CActiveRecord để lưu trữ c&aacute;c gi&aacute; trị tương ứng với Config cho ch&iacute;nh CActiveRecord đ&oacute;. Nhờ vậy, tiết kiệm được 1 model.</p>\r\n', 1309911724, 1309911742, 1, 11, 'configure, config, model');

